import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReviewList from '../components/ReviewList';
import ReviewForm from '../components/ReviewForm';
import bookwall from '../assets/bookwall.jpg'; // Import the image

const HomePage = () => {
    const [reviews, setReviews] = useState([]);

    // Fetch reviews from the backend
    useEffect(() => {
        axios
            .get('http://localhost:5000/reviews')
            .then((response) => {
                setReviews(response.data);
            })
            .catch((error) => {
                console.error('Error fetching reviews:', error);
            });
    }, []);

    return (
        <div
            style={{
                backgroundImage: `url(${bookwall})`,  // Set the background image
                backgroundSize: 'cover',  // Ensure the image covers the whole page
                backgroundPosition: 'center',  // Center the image
                backgroundAttachment: 'fixed',  // Fix the background when scrolling
                opacity: 0.75,  // Reduce opacity of the image
                minHeight: '100vh',  // Make sure the background covers the entire height of the page
            }}
        >
            {/* Title Strip */}
            <div
                style={{
                    backgroundColor: '#4CAF50', // Light emerald green
                    height: '100px', 
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    fontFamily: 'Creepster, cursive', 
                    color: '#fff', 
                    fontSize: '1.7rem',
                    textAlign: 'center',
                }}
            >
                <h1>ReadRate</h1>
            </div>

            <ReviewForm setReviews={setReviews} />
            <ReviewList reviews={reviews} setReviews={setReviews} />
        </div>
    );
};

export default HomePage;
