import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Box, Typography } from '@mui/material';

const ReviewForm = ({ setReviews }) => {
    const [bookTitle, setBookTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [rating, setRating] = useState(1);
    const [reviewText, setReviewText] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Check for empty fields
        if (!bookTitle || !author || !rating || !reviewText) {
            setErrorMessage('Please complete all fields.');
            return;
        }

        // Check if rating is between 1 and 5
        if (rating < 1 || rating > 5) {
            setErrorMessage('Rating must be between 1 and 5.');
            return;
        }

        // Check if author name contains any number or special character
        const authorRegex = /^[a-zA-Z\s]*$/;
        if (!authorRegex.test(author)) {
            setErrorMessage('Author name can only contain letters and spaces.');
            return;
        }

        // Clear error message and proceed with form submission
        setErrorMessage('');

        const newReview = {
            bookTitle,
            author,
            rating,
            reviewText,
        };

        // Send POST request to backend
        axios
            .post('http://localhost:5000/reviews', newReview)
            .then((response) => {
                setReviews((prevReviews) => [...prevReviews, response.data]);
                setBookTitle('');
                setAuthor('');
                setRating(1);
                setReviewText('');
            })
            .catch((error) => {
                console.error('Error adding review:', error);
            });
    };

    return (
        <Box
            sx={{
                width: '80%', // Reduced width to allow space for the image
                margin: '1rem auto',
                padding: '2rem',
                borderRadius: '15px',
                boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                backgroundColor: '#f5f5f5',
                display: 'flex', // Added flexbox to create two columns
                justifyContent: 'space-between', // Distribute the sections
                alignItems: 'center', // Vertically align the content
                border: '3px solid #009966'
            }}
        >
            {/* Left Section for Image */}
            <Box
                sx={{
                    flex: 1, // Left section takes 50% of the space
                    paddingRight: '0 rem', // Reduced space between image and form
                    height: 'auto', // Allow the height to adjust dynamically
                }}
            >
                <img
                    src={require('../assets/bookgirl.png')} // Add the image
                    alt="Book Girl"
                    style={{
                        width: '70%', // Let the width adjust automatically
                        height: '70%', // Make the height match the form's height
                        objectFit: 'cover', // Ensures the image scales proportionally without distortion
                        borderRadius: '8px',
                    }}
                />
            </Box>

            {/* Right Section for Form */}
            <Box
                sx={{
                    flex: 1.5, // Right section takes 50% of the space
                    display: 'flex', // Use flexbox to align form elements
                    flexDirection: 'column', // Align form elements vertically
                    height: '100%', // Ensure the form takes up the full height
                }}
            >
                <Typography
                    variant="h4"
                    sx={{
                        textAlign: 'center',
                        fontFamily: 'Helvetica, Arial, sans-serif',
                        fontWeight: 'bold',
                        fontSize: '35px',
                        color: '#023020',
                        marginBottom: '1rem',
                    }}
                >
                    Rate and Review
                </Typography>

                {/* Error Message */}
                {errorMessage && (
                    <Typography
                        variant="body1"
                        color="error"
                        sx={{ textAlign: 'center', marginBottom: '1rem' }}
                    >
                        {errorMessage}
                    </Typography>
                )}

                <form onSubmit={handleSubmit}>
                    <TextField
                        label="Book Title"
                        variant="outlined"
                        value={bookTitle}
                        onChange={(e) => setBookTitle(e.target.value)}
                        required
                        margin="normal"
                        sx={{
                            width: '2in',
                            '& .MuiOutlinedInput-root': {
                                '& fieldset': {
                                    borderColor: '#009966',
                                },
                            },
                        }}
                    />
                    <TextField
                        label="Author"
                        variant="outlined"
                        fullWidth
                        value={author}
                        onChange={(e) => setAuthor(e.target.value)}
                        required
                        margin="normal"
                        error={!/^[a-zA-Z\s]*$/.test(author) && author !== ''}
                        helperText={
                            !/^[a-zA-Z\s]*$/.test(author) && author !== ''
                                ? 'Author name can only contain letters and spaces.'
                                : ''
                        }
                        sx={{
                            '& .MuiOutlinedInput-root': {
                                '& fieldset': {
                                    borderColor: '#009966',
                                },
                            },
                        }}
                    />
                    <TextField
                        label="Rating (1-5)"
                        variant="outlined"
                        type="number"
                        value={rating}
                        onChange={(e) => setRating(e.target.value)}
                        min="1"
                        max="5"
                        required
                        margin="normal"
                        error={rating < 1 || rating > 5}
                        helperText={rating < 1 || rating > 5 ? 'Rating must be between 1 and 5' : ''}
                        sx={{
                            '& .MuiOutlinedInput-root': {
                                '& fieldset': {
                                    borderColor: '#009966',
                                },
                            },
                        }}
                    />
                    <TextField
                        label="Review Text"
                        variant="outlined"
                        multiline
                        rows={4}
                        fullWidth
                        value={reviewText}
                        onChange={(e) => setReviewText(e.target.value)}
                        required
                        margin="normal"
                        sx={{
                            '& .MuiOutlinedInput-root': {
                                '& fieldset': {
                                    borderColor: '#009966',
                                },
                            },
                        }}
                    />
                    <Button
                        type="submit"
                        variant="contained"
                        disabled={
                            !bookTitle ||
                            !author ||
                            !rating ||
                            !reviewText ||
                            rating < 1 ||
                            rating > 5 ||
                            !/^[a-zA-Z\s]*$/.test(author)
                        }
                        sx={{
                            backgroundColor: '#45B76D', // Button color
                            color: '#fff', 
                            '&:hover': {
                                backgroundColor: '#3E9F5F', 
                            },
                            mt: 2, 
                            textAlign: 'center', 
                        }}
                    >
                        Submit Review
                    </Button>
                </form>
            </Box>
        </Box>
    );
};

export default ReviewForm;
