import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Box, Typography } from '@mui/material';

const ReviewForm = ({ setReviews }) => {
    const [bookTitle, setBookTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [rating, setRating] = useState(1);
    const [reviewText, setReviewText] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Check for empty fields
        if (!bookTitle || !author || !rating || !reviewText) {
            setErrorMessage('Please complete all fields.');
            return;
        }

        // Check if rating is between 1 and 5
        if (rating < 1 || rating > 5) {
            setErrorMessage('Rating must be between 1 and 5.');
            return;
        }

        // Check if author name contains any number or special character
        const authorRegex = /^[a-zA-Z\s]*$/;
        if (!authorRegex.test(author)) {
            setErrorMessage('Author name can only contain letters and spaces.');
            return;
        }

        // Clear error message and proceed with form submission
        setErrorMessage('');

        const newReview = {
            bookTitle,
            author,
            rating,
            reviewText,
        };

        // Send POST request to backend
        axios
            .post('http://localhost:5000/reviews', newReview)
            .then((response) => {
                setReviews((prevReviews) => [...prevReviews, response.data]);
                setBookTitle('');
                setAuthor('');
                setRating(1);
                setReviewText('');
            })
            .catch((error) => {
                console.error('Error adding review:', error);
            });
    };

    return (
        <Box
            sx={{
                width: '60%',
                margin: '1rem auto',
                padding: '2rem',
                borderRadius: '15px',
                boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                backgroundColor: '#f5f5f5',
            }}
        >
            <Typography
                variant="h4"
                sx={{
                    textAlign: 'center',
                    fontFamily: 'Comic Sans MS, sans-serif',
                    color: '#1976d2',
                    marginBottom: '1rem',
                }}
            >
                Rate and Review
            </Typography>

            {/* Error Message */}
            {errorMessage && (
                <Typography
                    variant="body1"
                    color="error"
                    sx={{ textAlign: 'center', marginBottom: '1rem' }}
                >
                    {errorMessage}
                </Typography>
            )}

            <form onSubmit={handleSubmit}>
                <TextField
                    label="Book Title"
                    variant="outlined"
                    fullWidth
                    value={bookTitle}
                    onChange={(e) => setBookTitle(e.target.value)}
                    required
                    margin="normal"
                />
                <TextField
                    label="Author"
                    variant="outlined"
                    fullWidth
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    required
                    margin="normal"
                    error={!/^[a-zA-Z\s]*$/.test(author) && author !== ''}
                    helperText={
                        !/^[a-zA-Z\s]*$/.test(author) && author !== ''
                            ? 'Author name can only contain letters and spaces.'
                            : ''
                    }
                />
                <TextField
                    label="Rating (1-5)"
                    variant="outlined"
                    type="number"
                    value={rating}
                    onChange={(e) => setRating(e.target.value)}
                    min="1"
                    max="5"
                    required
                    margin="normal"
                    error={rating < 1 || rating > 5}
                    helperText={rating < 1 || rating > 5 ? 'Rating must be between 1 and 5' : ''}
                />
                <TextField
                    label="Review Text"
                    variant="outlined"
                    multiline
                    rows={4}
                    fullWidth
                    value={reviewText}
                    onChange={(e) => setReviewText(e.target.value)}
                    required
                    margin="normal"
                />
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    fullWidth
                    disabled={
                        !bookTitle ||
                        !author ||
                        !rating ||
                        !reviewText ||
                        rating < 1 ||
                        rating > 5 ||
                        !/^[a-zA-Z\s]*$/.test(author)
                    }
                >
                    Submit Review
                </Button>
            </form>
        </Box>
    );
};

export default ReviewForm;
